describe VirtualsController do

  # This should return the minimal set of attributes required to create a valid
  # Virtual. As you add validations to Virtual, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all virtuals as @virtuals" do
      virtual = Virtual.create! valid_attributes
      get :index
      assigns(:virtuals).should eq([virtual])
    end
  end

  describe "GET show" do
    it "assigns the requested virtual as @virtual" do
      virtual = Virtual.create! valid_attributes
      get :show, :id => virtual.id.to_s
      assigns(:virtual).should eq(virtual)
    end
  end

  describe "GET new" do
    it "assigns a new virtual as @virtual" do
      get :new
      assigns(:virtual).should be_a_new(Virtual)
    end
  end

  describe "GET edit" do
    it "assigns the requested virtual as @virtual" do
      virtual = Virtual.create! valid_attributes
      get :edit, :id => virtual.id.to_s
      assigns(:virtual).should eq(virtual)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Virtual" do
        expect {
          post :create, :virtual => valid_attributes
        }.to change(Virtual, :count).by(1)
      end

      it "assigns a newly created virtual as @virtual" do
        post :create, :virtual => valid_attributes
        assigns(:virtual).should be_a(Virtual)
        assigns(:virtual).should be_persisted
      end

      it "redirects to the created virtual" do
        post :create, :virtual => valid_attributes
        response.should redirect_to(Virtual.last)
