        assigns(:virtual).should be_a_new(Virtual)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Virtual.any_instance.stub(:save).and_return(false)
        post :create, :virtual => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested virtual" do
        virtual = Virtual.create! valid_attributes
        # Assuming there are no other virtuals in the database, this
        # specifies that the Virtual created on the previous line
        # receives the :update_attributes message with whatever params are
        # submitted in the request.
        Virtual.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => virtual.id, :virtual => {'these' => 'params'}
      end

      it "assigns the requested virtual as @virtual" do
        virtual = Virtual.create! valid_attributes
        put :update, :id => virtual.id, :virtual => valid_attributes
